.searchlist li<js>
result
   .toArray()
   .slice(0, 3)
   .map(function (el){
    var url = String(el.select('a').eq(0).attr('href')).replace(
      /^\/([^\/])/,
      baseUrl.match(/(.+\/\/.+?\/)/)[1] + '$1'
    );
    var J = org.jsoup.Jsoup.parse(String(java.ajax(url)));
    var og = (selector) =>
      String(J.select('[property="og:' + selector + '"]').attr('content'));

    return {
      name: og('novel:book_name').replace(/(全文|小说|免费|最新章节).*|（.*）/,''),
      author: og('novel:author'),
      kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/,''),
      latest: J.select('.bookcover p').eq(4).text(),
      cover: og('image'),
      wordCount: parseInt(J.select('.num').eq(0).text().replace('字','')/10000)+'万',  
      intro: og('description').replace(/.*简介：|各位书友.*/,'').replace(/\s+/g,''),
      url: url,
    };
  });
</js>

